mapper.update:
UPDATE review
SET content = #{content},
    tour_id=#{tourId}
WHERE review_id = #{reviewId};

mapper.select:
SELECT review_id, user_id
FROM review_like
WHERE user_id = #{userId}
  AND review_id = #{reviewId}

mapper.update:
UPDATE review
SET like_count = like_count + #{count}
WHERE review_id = #{reviewId};

mapper.delete:
DELETE
FROM review_like
WHERE review_id = #{reviewId}
  AND user_id = #{userId};
