mapper.select:
SELECT COUNT(*)
FROM users
WHERE username = #{username};

mapper.update:
UPDATE users
username = #{username}, email = #{email}, password = #{password}, nickname = #{nickname}, profile_image = #{profileImage}, provider_type = #{providerType}, role_type = #{roleType}, is_email_verified = #{isEmailVerified},
city_code = #{cityCode},
town_code = #{townCode}
WHERE user_id = #{userId};

UPDATE users
SET is_locked = !is_locked
WHERE user_id = #{userId};
